widgets.onCreate:
setInterval(()=>{
  var d = new Date()
  set(this,d.getUTCHours()+2 + ':' + String(d.getUTCMinutes()).padStart(2,'0'))
},1000)

widgets.props:
JS{

  var methods = OSC{/route_methods, 0, false} || {}
  var data = methods[$]
  var props = {
    label: data.label,
    html: data.html,
    mode: 'momentary',
    address: '/OpenStageControl/call',
    preArgs: ['route_method', data.method],
  }

  return props
}

widgets.onValue:
var bass = value.filter(x=>x.indexOf('Low')===0||x.includes('Sampler')),
    bvals = {}
for (var s of bass) {
  var label = s.includes('Sampler') ? s : s.substr(4)
  label = label.replace(/([A-Z])/g, ' $1').trim()
  bvals[label] = s
}
setVar('keyboard_low_switch', 'values', bvals)


var high = value.filter(x=>x.indexOf('Low') !== 0),
    hvals = {}
for (var s of high) {
  var label = s.replace(/^Z/g, '').replace(/([A-Z])/g, ' $1').trim()
  hvals[label] = s
}
setVar('keyboard_hi_switch', 'values', hvals)

set('keyboard_low_switch', get('keyboard_current_sound'))
set('keyboard_hi_switch', get('keyboard_current_sound'))

if (value) {
  send('/JmjKeyboard/call', 'set_sound', value)
  if (get('follow_lead')) {
    send('/Synths/call', 'set_lead', value)
  }
  set('parent', 0)
}
