widgets.css:
> inner {
  border: 32rem solid var(--strip-bg);;
  border-top: 0;
}
.navigation {
  order: 1;
  margin-left:1rem;  
}
.section-label {
  margin: 0;
}

widgets.onValue:
var [param, val] = value
var n = getProp('parent', 'variables').n


if (param == 'length') {
  setVar('sl_position_' + n, 'length', val)
} else if (param == 'position') {
  if (!getVar('sl_recording_' + n, 'active')) set('sl_position_' + n, val)
} else {
  setVar('sl_' + param + '_' + n, 'active', val)
}

if (param == 'recording' && val == 1) {
  set('sl_position_' + n, 0)
}

tabs.css:
.navigation {
  overflow: auto;
  order:1;
  border-left: 1rem solid var(--color-background);
}
.tablink {
  min-width: 200rem;
  justify-content:start;
  min-height: 30rem;

}
.modal-container > label {
display: block;
text-overflow: ellipsis;
line-height: 24rem;
padding: 0 4rem;
}
