widgets.onValue:
var [param, val] = value
var n = getProp('parent', 'variables').n


if (param == 'position') {
  if (!getVar('sl_recording_' + n, 'active')) set('sl_position_' + n, val)
} else {
  setVar('sl_' + param + '_' + n, 'active', val)
}

if (param == 'recording') {
  if (val === 1) set('sl_position_' + n, 0)
}

widgets.props:
JS{

  var methods = OSC{/route_methods, 0, false} || {}
  var data = methods[$]
  var props = {
    label: data.label,
    html: data.html,
    mode: 'momentary',
    address: '/OpenStageControl/call',
    preArgs: ['route_method', data.method],
  }

  return props
}
