content.css:
.input-container {
  --border-radius: 3rem
}
.menu-container,
.modal-container,
.button-container {
  --color-fill: rgba(86,118,140,0.45);
  --color-stroke: var(--color-fill);
  --alpha-fill-off: 1;
  --border-radius: 3rem;
  --widget-padding:0;
}
.button-container, .modal-container > .toggle {
    cursor: pointer;
}
.mixer .button-container label {
    padding-top:2rem;

}
.button-container.on {
  --color-fill: var(--color-widget)
}
.popup-wrapper {
  border-radius:4rem;
}
.disabled {
  cursor: not-allowed;
  opacity:0.5;
}
.disabled .widget {
  pointer-events: none;
}
.locked {
  filter: grayscale(0.25);
  pointer-events: none;
}
.carved:after {
  --alpha-stroke: 1
}

menu {
  margin-top:-3rem;
  margin-left:-3rem;
  border-radius:4rem;
}
menu .item:hover {
  background:rgba(255,255,255,0.1);
}

widgets.css:
svg {
  top: 8rem;
  left: 50%;
  fill:rgba(30,34,40,1);
  margin-left:-12rem;
}

:host {
    transform: scaleY(-1.25) scaleX(1.25);
    min-width: 40rem!important;
    z-index:0;
}

widgets.onCreate:
setInterval(()=>{
  var d = new Date(),
      h = d.getHours().toLocaleString().padStart(2,"0"),
      m = d.getMinutes().toLocaleString().padStart(2,"0")
  set(this, `${h}:${m}`)
},1000)

widgets.props:
JS{
  var props = {widgetId: 'strip_FOH', props: {variables: {}}},
      cProps = props.props,
      cVars = cProps.variables,
      names = Object.keys(@{strip_list}),
      colors = Object.values(@{strip_list})

  cVars.name = names[$]
  cProps.colorWidget = colors[$]

  return props
}

widgets.css:
class: strip;
.label {
  height: 60rem;
  line-height: 60rem;
  text-align: center;
  border-bottom: 1rem solid rgba(0,0,0,0.3);
  xborder-top: 4rem solid var(--color-widget);
  text-transform: uppercase;
  letter-spacing: 2rem;
  text-shadow: 0 0 1rem var(--color-widget);
}
.label:after {
  bworder-top: 4rem solid var(--color-widget);
  content:"";
  opacity: 0.5;
  width:100%;
  position:absolute;left:0;
}

.indic {
  position:absolute;
  width: 4rem;
  height: 4rem;
  border-radius: 100%;
  top: 8rem;
  right: 8rem;
  background: rgba(0,0,0,0.5);
  overflow:hidden;
  box-shadow: 0 0 0 1rem rgba(0,0,0,0.5);
  pointer-events: none;
}
.indic:after {
  position:absolute;
  content:"";
  top:0;left:0;width:100%;height:100%;
  background: var(--color-widget);
  opacity: #{OSC{/@{this.comments}} ? 1 :  0};
}

.indic {
  position:absolute;
  width: 4rem;
  height: 4rem;
  border-radius: 100%;
  top: 8rem;
  right: 8rem;
  background: rgba(0,0,0,0.5);
  overflow:hidden;
  box-shadow: 0 0 0 1rem rgba(0,0,0,0.5);
}
.indic:after {
  position:absolute;
  content:"";
  top:0;left:0;width:100%;height:100%;
  background: var(--color-widget);
  opacity: #{OSC{/@{this.comments}} ? 1 :  0};
}

:host {
  opacity: #{OSC{/Outputs/@{parent.variables.name}/ACE%20Compressor} ? 1 :  0.35};
}
canvas {
  margin: 4rem 30rem;
}
.axis {
  position: absolute;
  top:4rem;left:30rem;bottom:4rem;right:30rem;
  opacity: 0.5;
  border-color:var(--color-widget);
}
.axis:before {
  position:absolute;
  content: "";
  top: calc(14.28% + 3rem);
  left:0; right: 0;
  border-top: 1rem dashed var(--color-widget);;
}
.axis:after {
  position:absolute;
  content: "";
  right: calc(14.28% + 1rem);
  top:0; bottom: 0;
  border-left: 1rem dashed var(--color-widget);;
}

class: pan-knob;
.lr:after,
.lr:before {
  position:absolute;
  top:0;line-height:80rem;
  width: 25%;
  text-align: center;
  opacity: 0.5;
}
.lr:before {
  content: "L";
  left:0;
}
.lr:after {
  content: "R";
  right: 0;
}

widgets.value:
JS{

var level = @{prefader_level_@{parent.variables.name}}
if (@{pfl_@{parent.variables.name}} === 0) {
  if (@{gain_@{parent.variables.name}} === -70 ||  @{mute_@{parent.variables.name}}) {
    level = -70
  } else {
    level += @{gain_@{parent.variables.name}}
  }
}
return level

}
